import React, { useState, useEffect } from 'react';
import "./weatherapp.css";

const WeatherApp = () => {
  const [temperature, setTemperature] = useState("");

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const API_KEY = "c743e897ce5b7dc65494d5d4083e4190";
          const API_URL = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}&units=metric`;

          fetch(API_URL)
            .then((response) => response.json())
            .then((data) => {
              setTemperature(data.main.temp + "°C"); // Assuming the temperature data is in Celsius
            })
            .catch((error) => {
              console.log("Error fetching temperature:", error);
            });
        },
        (error) => {
          console.log("Error getting location:", error);
        }
      );
    } else {
      console.log("Geolocation is not supported by this browser.");
    }
  }, []);

  return (
    <div className="maindiv">
      <header>Weather</header>
      <br />
      <div className="hr">
        <hr />
      </div>
      <div className="grid-container">
        <div className="big-div">
          <p>{temperature}</p>
          {/* Add the weather icon here */}
          {/* Example: <img src={weatherIconURL} alt="Weather Icon" /> */}
        </div>
        <div className="inner-div">
          <p>Inner Div 1</p>
          <p>Lorem ipsum dolor sit amet.</p>
        </div>
        <div className="inner-div">
          <p>Inner Div 2</p>
          <p>Lorem ipsum dolor sit amet.</p>
        </div>
        <div className="inner-div">
          <p>Inner Div 3</p>
          <p>Lorem ipsum dolor sit amet.</p>
        </div>
        <div className="inner-div">
          <p>Inner Div 4</p>
          <p>Lorem ipsum dolor sit amet.</p>
        </div>
      </div>
    </div>
  );
}

export default WeatherApp;
