import React, { useState, useEffect } from 'react';
import "./weatherapp.css";

const weatherapp = () => {
    const [temperature, setTemperature] = useState("");

  useEffect(() => {
    fetch("API_URL") // Replace "API_URL" with the actual API endpoint for fetching the temperature
      .then((response) => response.json())
      .then((data) => {
        setTemperature(data.main.temp + "°C"); // Assuming the temperature data is in Celsius
      })
      .catch((error) => {
        console.log("Error fetching temperature:", error);
      });
  }, []);
    return (
        <div className="maindiv">
            <header>Weather</header>
            <br/>
            <div className='hr'>
                <hr/>
            </div>
            <div class="grid-container">
  <div class="big-div">
    <p>{temperature}</p>
  </div>
  <div class="inner-div">
    <p>Inner Div 1</p>
    <p>Lorem ipsum dolor sit amet.</p>
  </div>
  <div class="inner-div">
    <p>Inner Div 2</p>
    <p>Lorem ipsum dolor sit amet.</p>
  </div>
  <div class="inner-div">
    <p>Inner Div 3</p>
    <p>Lorem ipsum dolor sit amet.</p>
  </div>
  <div class="inner-div">
    <p>Inner Div 4</p>
    <p>Lorem ipsum dolor sit amet.</p>
  </div>
</div>
</div>
    )
}
export default weatherapp;
