import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Todo = ({ username }) => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    // Fetch the user's todos from the server
    const fetchTodos = async () => {
      try {
        const response = await axios.get(`http://localhost:9002/todos/${username}`);
        setTodos(response.data.todos);
      } catch (error) {
        console.log('Error fetching todos:', error);
      }
    };

    fetchTodos();
  }, [username]);

  const handleInputChange = (e) => {
    setNewTodo(e.target.value);
  };

  const handleAddTodo = async () => {
    try {
      const response = await axios.post(`http://localhost:9002/todos/${username}`, { todo: newTodo });
      setTodos([...todos, response.data.todo]);
      setNewTodo('');
    } catch (error) {
      console.log('Error adding todo:', error);
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:9002/todos/${username}/${id}`);
      const updatedTodos = todos.filter((todo) => todo._id !== id);
      setTodos(updatedTodos);
    } catch (error) {
      console.log('Error deleting todo:', error);
    }
  };

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <div>
        <input type="text" value={newTodo} onChange={handleInputChange} placeholder="Enter a new todo" />
        <button onClick={handleAddTodo}>Add Todo</button>
      </div>
      <ul>
        {todos.map((todo) => (
          <li key={todo._id}>
            {todo.task}
            <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Todo;
